function SocialMedia(props: { children: ReactNode; href: string }) {
  return (
    <NextLink target="_blank" href={props.href} borderRadius="full" replace>
      <Center>
        <svg
          width="50px"
          height="50px"
          viewBox="0 0 32 32"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            opacity="0.1"
            fillRule="evenodd"
            clipRule="evenodd"
            d="M0 16C0 7.16344 7.16344 0 16 0C24.8366 0 32 7.16344 32 16C32 24.8366 24.8366 32 16 32C7.16344 32 0 24.8366 0 16Z"
            fill="white"
          />
        </svg>
        <Box position="absolute">{props.children}</Box>
      </Center>
    </NextLink>
  );
}

